import { ReactNode } from 'react';

// Define as props do PowerTable
interface PowerTableProps {
    children: ReactNode;
    title?: string;
    tableId: string;
    columns: any[];
    checkboxSelection?: boolean;
    onSelected?: (selectedItems: any[]) => void;
    className?: string;
    disableColumnSorting?: boolean;
    disableLastColumnSorting?: boolean;
    disableColumnResize?: boolean;
    disableColumnFilter?: boolean;
    disableColumnMenu?: boolean;
    disableSearchFilter?: boolean;
    disableExport?: boolean;
    disableAutosize?: boolean;
    columnHeaderHeight?: number | null;
    hideFooter?: boolean;
    pageSizeOptions?: number[];
    localeText?: Record<string, string>;
    actionButtons?: React.ReactNode[];
}

// Define as props do Row
interface RowProps {
    children: ReactNode;
    className?: string;
    id?: string | number;
    onClick?: (event: React.MouseEvent<HTMLTableRowElement>) => void;
    onDoubleClick?: (event: React.MouseEvent<HTMLTableRowElement>) => void;
}

// Define as props do Cell
interface CellProps {
    children: ReactNode;
    className?: string | string[];
    id?: string | number | null;
}

// Declaração das funções de configuração
export function configureGlobalLocaleText(newLocaleText: Record<string, string>): void;
export function configureUserLocale(newLocale: Record<string, string>): void;

// Exportações correspondentes ao seu index.js
export const PowerTable: (props: PowerTableProps) => ReactNode;
export const Row: (props: RowProps) => ReactNode;
export const Cell: (props: CellProps) => ReactNode;
