import { ReactNode } from 'react';

// Define as props do MediaView
export interface MediaViewProps {
    file: any[]; // Você pode definir um tipo mais específico ao invés de 'any[]' se souber a estrutura do objeto
    onRename?: (value: any) => object; // Função para renomear o arquivo que retorna um objeto
    onDelete?: (value: any) => object; // Função para deletar o arquivo que retorna um objeto
}

// Definindo a interface para as props do MediaPreview
interface MediaPreviewProps {
    file?: any; // Você pode definir um tipo mais específico ao invés de 'any' se souber a estrutura do objeto
    displayFooter?: boolean; // Opção para mostrar o rodapé
    hideDropdown?: boolean; // Opção para esconder o dropdown
    style?: React.CSSProperties; // Estilos inline
    cover?: boolean; // Opção para cobrir a área
    className?: string; // Classe CSS adicional
    onClick?: () => void; // Função de clique
    onRename?: (value: any) => object; // Função para renomear o arquivo que retorna um objeto
    onDelete?: (value: any) => object; // Função para deletar o arquivo que retorna um objeto
}

// Exportações correspondentes ao seu index.js
export const MediaView: (props: MediaViewProps) => ReactNode;
export const MediaPreviewProps: (props: MediaPreviewProps) => ReactNode;
